import{_ as i,o as l,c as a,a as e}from"./app-49c07030.js";const r={},h=e('<h1 id="telelgram限制" tabindex="-1"><a class="header-anchor" href="#telelgram限制" aria-hidden="true">#</a> Telelgram限制</h1><p>Telegram 虽然以各种口号宣传自己，但它依然有很多的规则和限制，下面提供详细信息。</p><h2 id="账号" tabindex="-1"><a class="header-anchor" href="#账号" aria-hidden="true">#</a> 账号</h2><ul><li>用户名：5~32字符</li><li>姓氏：1~32字符</li><li>名字：1~32字符</li><li>个人简介：0~70字符</li><li>可加入群组和频道：0~500</li><li>创建公开群组和频道数：0~10</li><li>同时登录账号数：0~3</li><li>收藏贴纸数：0~5</li><li>收藏GIF数：0~200</li><li>视频头像时长：0~10秒</li><li>创建频道和群组：0~50/天</li><li>自动注销不活跃账号：1月/3月/6月/12月</li></ul><h2 id="频道" tabindex="-1"><a class="header-anchor" href="#频道" aria-hidden="true">#</a> 频道</h2><ul><li>关注者数：0~∞</li><li>管理员数：0~50</li><li>用户名：5~32字符</li><li>名称：1~255字符</li><li>简介：0~255字符</li><li>标题：0~128字符</li><li>删除频道：0~1000关注(大于1000关注的频道不能删除)</li><li>不加入查看私有频道(关注者数&gt;200)：0~5分钟</li><li>邀请成员加入频道：0~200人</li><li>&quot;统计数据&quot;可见的关注者数：0~50人</li><li>更改用户名生效时长：15~30分钟</li></ul><h2 id="群组" tabindex="-1"><a class="header-anchor" href="#群组" aria-hidden="true">#</a> 群组</h2><ul><li>群成员：0~20万</li><li>广播群成员：0~∞</li><li>管理员数：0~50</li><li>机器人数：0~20</li><li>客户端可见消息数：0~100万</li><li>用户名：5~32字符</li><li>名称：1~255字符</li><li>简介：0~255字符</li><li>标题：0~128字符</li><li>管理员头衔：0~16字符</li><li>编辑已发送消息：0~48小时</li><li>管理员编辑已发送消息：0~∞</li><li>单条消息@成员数：0~50</li><li>&quot;统计数据&quot;可见的成员数：0~500人</li><li>可设置群组贴纸成员数：&gt;100人</li><li>转为广播群：&gt;199000人</li><li>消息已读成员列表：&lt;100人</li><li>消息已读成员列表有效时长：&lt;7天</li><li>更改用户名生效时长：15~30分钟</li></ul><h2 id="消息" tabindex="-1"><a class="header-anchor" href="#消息" aria-hidden="true">#</a> 消息</h2><ul><li>单个文件大小限制：1B~2000MB</li><li>媒体说明的字数限制：0~1024</li><li>语音聊天听众数：0~∞</li><li>视频聊天/广播听众数：0~∞</li><li>视频聊天/广播：0~30同时广播</li><li>可选的预约时长：&lt;7天</li></ul><h2 id="对话" tabindex="-1"><a class="header-anchor" href="#对话" aria-hidden="true">#</a> 对话</h2><ul><li>分组上限数：0~10</li><li>分组中的对话数：1~100</li><li>&quot;对话&quot;中置顶对话数：0~5</li><li>分组中置顶对话数：0~100</li></ul><h2 id="附近的人和群组" tabindex="-1"><a class="header-anchor" href="#附近的人和群组" aria-hidden="true">#</a> 附近的人和群组</h2><ul><li>可见范围：0~12公里</li><li>可见用户数：0~100</li><li>可见本地群数：0~10</li></ul><h2 id="机器人" tabindex="-1"><a class="header-anchor" href="#机器人" aria-hidden="true">#</a> 机器人</h2><ul><li>可创建机器人数：0~20</li><li>用户名：5~32字符</li><li>简介：0~512字符</li><li>描述：0~120字符</li><li>键盘按钮数：0~100</li><li>markup数据：0~10KB</li><li>上传单个文件大小限制(不使用本地BotAPI后端)：1B~50MB</li><li>上传单个文件大小限制(使用本地BotAPI后端)：1B~2000MB</li><li>下载单个文件大小限制(不使用本地BotAPI后端)：1B~20MB</li><li>上传单个文件大小限制(使用本地BotAPI后端)：1B~2000MB</li><li>在群组中发送消息频率：0~20条/秒</li><li>命令数：0~100</li><li>命令长度：1~32字符</li><li>命令描述长度：0~256字符</li><li>命令的元数据长度：0~64字符</li><li>弹窗提示文本：0~200字符</li><li>inline查询结果数：0~50条</li></ul><h2 id="贴纸" tabindex="-1"><a class="header-anchor" href="#贴纸" aria-hidden="true">#</a> 贴纸</h2><ul><li>名称：1~64字符</li><li>链接：0~62字符</li><li>收藏贴纸数：0~5</li><li>添加贴纸包数：0~200</li><li>单个贴纸包：0~120张静态贴纸 / 0~50张动态贴纸</li><li>贴纸比例：静态贴纸：长512px宽≤512px 或 长≤512px宽512px，动态贴纸：长512px宽512px</li><li>动态贴纸持续时间：0~3秒</li><li>贴纸大小：静态贴纸：≤512KB，动态贴纸：≤64KB</li><li>帧率(FPS)：30/60</li></ul>',18),t=[h];function d(n,u){return l(),a("div",null,t)}const o=i(r,[["render",d],["__file","limit.html.vue"]]);export{o as default};
